import axios from "axios";
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.css";

function App() {
  const [news, setNews] = useState([]);
  const [bookmarks, setBookmarks] = useState([]);

  const handleBookmark = (article) => {
    const updatedBookmarks = [...bookmarks, article];
    setBookmarks(updatedBookmarks);
    saveBookmarks(updatedBookmarks);
  };

  const handleRemoveBookmark = (article) => {
    const updatedBookmarks = bookmarks.filter(
      (item) => item.title !== article.title
    );
    setBookmarks(updatedBookmarks);
    saveBookmarks(updatedBookmarks);
  };

  const saveBookmarks = (bookmarks) => {
    localStorage.setItem("bookmarks", JSON.stringify(bookmarks));
  };

  const retrieveBookmarks = () => {
    const savedBookmarks = localStorage.getItem("bookmarks");
    if (savedBookmarks) {
      setBookmarks(JSON.parse(savedBookmarks));
    }
  };

  useEffect(() => {
    axios
      .get(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=fd4ef2f1e25b46a692448544e2d9f3f4"
      )
      .then((res) => {
        setNews(res.data.articles);
      });
    retrieveBookmarks();
  }, []);

  return (
    <>
      <div className="container my-5">
        <div className="row text-center">
          {news.map((val) => {
            return (
              <div className="col my-3">
                <div
                  className="card"
                  style={{ width: "18rem", height: "34rem" }}
                >
                  <img src={val.urlToImage} className="card-img-top" alt="" />
                  <div className="card-body">
                    <h6 className="card-title">{val.title}</h6>
                    <p className="card-text">{val.description}</p>
                    <a href={val.url} className="btn btn-primary">
                      More Details
                    </a>
                    {bookmarks.some(
                      (bookmark) => bookmark.title === val.title
                    ) ? (
                      <button onClick={() => handleRemoveBookmark(val)}>
                        Remove Bookmark
                      </button>
                    ) : (
                      <button onClick={() => handleBookmark(val)}>
                        Bookmark
                      </button>
                    )}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <h2>Bookmarks</h2>
      {bookmarks.map((bookmark, index) => (
        <div key={index}>
          <h3>{bookmark.title}</h3>
          <p>{bookmark.description}</p>
        </div>
      ))}
    </>
  );
}
export default App;
